# source: http://localhost:8080/query
# timestamp: Thu Aug 23 2018 10:30:37 GMT+1000 (AEST)

"""This directive does magical things"""
directive @magic(kind: Int) on FIELD_DEFINITION

enum DATE_FILTER_OP {
  EQ
  NEQ
  GT
  GTE
  LT
  LTE
}

input DateFilter {
  value: String!
  timezone: String = "UTC"
  op: DATE_FILTER_OP = EQ
}

type Element {
  child: Element!
  error: Boolean!
  mismatched: [Boolean!]
}

enum ErrorType {
  CUSTOM
  NORMAL
}

type Query {
  path: [Element]
  date(filter: DateFilter!): Boolean!
  viewer: Viewer
  jsonEncoding: String!
  error(type: ErrorType = NORMAL): Boolean!
  complexity(value: Int!): Boolean!
}

type User {
  name: String!
  likes: [String!]!
}

type Viewer {
  user: User
}

"The `Upload` scalar type represents a multipart file upload."
scalar Upload

"The `File` type, represents the response of uploading a file."
type File {
    id: Int!
    name: String!
    content: String!
}

"The `UploadFile` type, represents the request for uploading a file with certain payload."
input UploadFile {
    id: Int!
    file: Upload!
}

"The `Query` type, represents all of the entry points into our object graph."
type Query {
    empty: String!
}

"The `Mutation` type, represents all updates we can make to our data."
type Mutation {
    singleUpload(file: Upload!): File!
    singleUploadWithPayload(req: UploadFile!): File!
    multipleUpload(files: [Upload!]!): [File!]!
    multipleUploadWithPayload(req: [UploadFile!]!): [File!]!
}

type Query {
  todos: [Todo!]!
}

type Mutation {
  createTodo(input: NewTodo!): Todo!
}

