schemas.content:
input NewUser {
  name: String!
  email: String!
  password: String!
}

type UserToken {
  Token: String!
}

type IsLogin {
  isLogin: Boolean!
}

input User {
  email: String!
  password: String!
}

input NewGalleryInput {
  title: String!
}

input UpdateGalleryInput {
  id: Int!
  title: String!
}

input DeleteImageInput {
  id: Int!
  fileName: String!
}

input DeleteGalleryInput {
  id: Int!
}

input GalleryID {
  id: Int!
}

type Image {
  galleryID: Int!
  fileName: String!
  path: String!
}

type Gallery {
  id: Int!
  title: String!
  images: [Image]
}

type Mutation {
  signup(input: NewUser!): UserToken!
  login(input: User!): UserToken!
  logout: UserToken!
  createGallery(input: NewGalleryInput!): Gallery!
  updateGallery(input: UpdateGalleryInput!): Gallery!
  deleteImage(input: DeleteImageInput!): Gallery!
  deleteGallery(input: DeleteGalleryInput!): Boolean!
}

type Query {
  isLogin: IsLogin!
  listGalleries: [Gallery]!
  showGallery(input: GalleryID!): Gallery!
}
