schemas.content:
type Item {
  id: ID!
  name: String
}

type Query {
  items: [Item]
}

type Subscription {
  changed: Item
}

type Todo {
  id: ID!
  description: String!
  done: Boolean
}

type Query {
  todos: [Todo]
  todo(id: ID!): Todo
}

type TodoAddResult {
  result: Todo
  failure: AddTodoFailure
}

type TodoToggleDoneResult {
  result: Todo
  failure: ToggleDoneFailure
}

type TodoRemoveResult {
  result: Todo
  failure: RemoveTodoFailure
}

type ToggleDoneFailure {
  message: String!
}

type RemoveTodoFailure {
  message: String!
}

type AddTodoFailure {
  message: String!
}

input AddTodoInput {
  description: String!
}

type Mutation {
  todoAdd(input: AddTodoInput!): TodoAddResult
  todoRemove(id: ID!): TodoRemoveResult
  todoToogleDone(id: ID!): TodoToggleDoneResult
}

type Todo {
  id: ID!
  rev: ID!
  description: String!
  done: Boolean
}

type Query {
  todos: [Todo]
}

type AddTodoResult {
  result: Todo
  failure: AddTodoFailure
}

type ResetResult {
  result: ResetStatus
  failure: ResetFailure
}

type ToggleDoneResult {
  result: Todo
  failure: ToggleDoneFailure
}

type RemoveTodoResult {
  result: Todo
  failure: RemoveTodoFailure
}

type ResetStatus {
  status: String!
}

type ResetFailure {
  message: String!
}

type ToggleDoneFailure {
  message: String!
}

type RemoveTodoFailure {
  message: String!
}

type AddTodoFailure {
  message: String!
}

input AddTodoInput {
  description: String!
}

type Mutation {
  addTodo(input: AddTodoInput!): AddTodoResult
  removeTodo(id: ID!): RemoveTodoResult
  toogleDone(id: ID!): ToggleDoneResult
  reset: ResetResult
}
