schemas.content:
type Query {
  viewer: Viewer!
}

type Viewer {
   id: ID!
   total: Int!
   notes: [Note]
}

type Note {
  id: ID!
  content: String!
  date: String!
}

type Mutation {
  addNote(input: AddNoteInput): NoteMutationResult
  removeNote(input: RemoveNoteInput): NoteMutationResult
  editNote(input: EditNoteInput): NoteMutationResult
}

type NoteMutationResult {
  note: Note!
}

input AddNoteInput {
  content: String!
}

input RemoveNoteInput {
  id: ID!
}

input EditNoteInput {
  id: ID!
  content: String
}
