schemas.content:
scalar Date

type Query {
  reservation(id:ID!): Reservation

  reservations: [Reservation!]!
}

type Mutation {
  createReservation(reservation:CreateReservationInput!): Reservation
  deleteReservation(id:ID!): ID
}


type Reservation {
  id: ID!
  name: String!
  hotelName: String!
  arrivalDate: Date!
  departureDate: Date!
  createdAt: Date!
  updatedAt: Date!
}


input CreateReservationInput {
  name: String!
  hotelName: String!
  arrivalDate: Date!
  departureDate: Date!
}
