# https://github.com/fostertheweb/skimp-server/blob/cd062829937825948be0ec5caaf420edaf75f634/src/schema.graphql:
# import { Account } from "src/plaid/accounts.graphql"
# import { Category, Transaction, TransactionInput } from "src/plaid/transactions.graphql"

type Query {
  accounts: [Account!]!
  categories: [Category!]!
  transactions(input: TransactionInput!): [Transaction!]!
}

type Mutation {
  exchangePublicToken(publicToken: String!): String!
}

# https://github.com/fostertheweb/skimp-server/blob/b8467322ae1787bb8083aec050eb7f6152964dfc/src/plaid/accounts.graphql:
type Account {
  accountId: String!
  balances: Balances
  mask: String!
  name: String!
  numbers: AccountNumbers
  officialName: String!
  subtype: AccountSubType!
  type: AccountType!
}

type ACH {
  account: String!
  accountId: String!
  routing: String!
  wireRouting: String!
}

type EFT {
  account: String!
  accountId: String!
  institution: String!
  branch: String!
}

type AccountNumbers {
  ach: [ACH!]
  eft: [EFT!]
}

enum AccountType {
  BROKERAGE
  CREDIT
  DEPOSITORY
  LOAN
  MORTGAGE
  OTHER
}

enum AccountSubType {
  CHECKING
  CASH_DIRECTORY
  SAVINGS
  CREDIT_CARD
}

enum ISOCurrencyCode {
  USD
  CAD
}

type Balances {
  available: Float
  current: Float
  limit: Float
  isoCurrencyCode: ISOCurrencyCode
  unofficialCurrencyCode: String
}

# https://github.com/fostertheweb/skimp-server/blob/b8467322ae1787bb8083aec050eb7f6152964dfc/src/plaid/transactions.graphql:
enum TransactionType {
  DIGITAL
  PLACE
  SPECIAL
  UNRESOLVED
}

type Category {
  categoryId: Int!
  group: String!
  hierarchy: [String!]!
}

type Location {
  address: String
  city: String
  state: String
  latitude: Float
  longitude: Float
}

type PaymentMeta {
  referenceNumber: Int
  ppdId: Int
  payeeName: String
}

input OptionsInput {
  accountIds: [String!]
  count: Int
  offset: Int
}

input TransactionInput {
  startDate: String!
  endDate: String!
  options: OptionsInput
}

type Transaction {
  transactionId: String!
  accountId: String!
  category: [String!]!
  categoryId: String!
  transactionType: TransactionType!
  name: String!
  amount: Float!
  date: String!
  pending: Boolean!
  pendingTransactionId: String
  accountOwner: String!
  location: Location
  paymentMeta: PaymentMeta
}

