# Schema
schema {
  query: Query
  mutation: Mutation
}

# Queries
type Query {
  # Query to get all the items of the logged in user with support for paging
  getItems(limit: Int, nextToken: String): ItemConnection
}

# Paged item result
type ItemConnection {
  items: [Item]
  nextToken: String
}

# Mutations
type Mutation {
  createItem(url: String!): Item
}

# Single item (our DynamoDB row)
type Item {
  item_counter: Int
  created_at: Int
  last_visit: Int
  slug: String
  url: String
  user_id: String
}

