schemas.content:
type Query {
  """
  Last.fm Api
  """
  lastfm: LastFM
}

type Date {
  "The date - Unix Time"
  date: String
  "The text version of the date"
  text: String
}

type Tag {
  "The name of the tag"
  name: String
  "The url to the last.fm page for the tag"
  url: String
}

type LastFM {
  "The last.fm user"
  user(
    "The last.fm username, this **must** be passed into the query else it will fail"
    username: String!
  ): User
}

