type User {
  id: Int!
  username: String!
  firstName: String!
  lastName: String!
  fullName: String!
  bio: String!
}

type Query {
  me: User
}

schema {
  query: Query
}

type User {
  id: Int!
  username: String!
  todos: [Todo]!
}

type Todo {
  id: Int!
  content: String!
  author: User!
}

type Query {
  me: User!
  user(id: Int!): User
  todo(id: Int!): Todo
}

input WriteUserInput {
  id: Int
  username: String!
}

input CreateTodoInput {
  id: Int!
  content: String!
  userId: Int!
}

type Mutation {
  writeUser(input: WriteUserInput): User
  createTodo(input: CreateTodoInput): Todo
}

schema {
  query: Query
  mutation: Mutation
}

type Query {
  users(page: Int): [User]!
  user(id: Int!): User!
}

type Album {
  user: User!
  id: Int!
  title: String!
  photos: [Photo]!
}

type Todo {
  user: User!
  id: Int!
  title: String!
  completed: Boolean!
}
