schemas.content:
enum EmailFrequencyEnum {
  DAILY
  WEEKLY
  NO_EMAILS
}

enum WatchTypeEnum {
  TAG
  TALENT
  VENUE
  EVENT
  EVENT_SERIES
}

enum WatchChangeEnum {
  ADD
  DELETE
}

type Preferences {
  emailFrequency: EmailFrequencyEnum!
}

type Watch {
  id: ID!
  label: String!
}

type WatchList {
  items: [Watch!]!
  version: Int!
}

type WatchSet {
  tag: WatchList
  event: WatchList
  eventSeries: WatchList
  talent: WatchList
  venue: WatchList
}

type Query {
  preferences: Preferences
  watches: WatchSet
}

type DeleteUserOutput {
  ok: Boolean!
}

input WatchChangeInput {
  changeType: WatchChangeEnum
  id: ID!
  label: String
}

input UpdateWatchesInput {
  watchType: WatchTypeEnum!
  newVersion: Int!
  changes: [WatchChangeInput!]!
}

type UpdateWatchesOutput {
  watches: WatchList
}

input UpdatePreferencesInput {
  emailFrequency: EmailFrequencyEnum!
}

type UpdatePreferencesOutput {
  ok: Boolean
}

type Mutation {
  deleteUser: DeleteUserOutput
  updateWatches(input: UpdateWatchesInput!): UpdateWatchesOutput
  updatePreferences(input: UpdatePreferencesInput!): UpdatePreferencesOutput
}

scalar IsoShortDate

type HeroImage {
  name: String!
  dominantColor: String!
  label: String!
}

type HeroImagesResult {
  nodes: [HeroImage!]!
}

type NamedClosureList {
  type: String!
  dates: [IsoShortDate!]!
}

type NamesClosuresResult {
  lists: [NamedClosureList!]!
}

type Query {
  heroImages: HeroImagesResult
  namedClosures: NamesClosuresResult
}

enum TagTypeEnum {
  MEDIUM
  STYLE
  GEO
  AUDIENCE
}

type TagDetail {
  tagType: TagTypeEnum!
  id: ID!
  label: String!
}

type TagsResult {
  nodes: [TagDetail!]!
}

type Query {
  tags(tagType: TagTypeEnum!): TagsResult
}

input CreateTagInput {
  tagType: TagTypeEnum!
  label: String!
}

type CreateTagOutput {
  node: TagDetail
}

input DeleteTagInput {
  tagType: TagTypeEnum!
  id: ID!
}

type DeleteTagOutput {
  ok: Boolean
}

type Mutation {
  createTag(input: CreateTagInput!): CreateTagOutput
  deleteTag(input: DeleteTagInput!): DeleteTagOutput
}

enum ImageTypeEnum {
  EVENT
  EVENT_SERIES
  TALENT
  VENUE
}

type Image {
  id: ID!
  imageType: ImageTypeEnum!
  sourceUrl: String!
  mimeType: String!
  width: Int!
  height: Int!
  dominantColor: String!
  ratio: Float!
  resizeVersion: Int!
  modifiedDate: String!
}

type ImageIteration {
  actionId: ID!
  iterationId: ID!
}

type ImageResult {
  node: Image
}

type Query {
  image(id: ID!): ImageResult
}

input CreateTagInput {
  id: ID!
  type: ImageTypeEnum!
  url: String!
}

type AddImageOutput {
  node: Image
}

type ReprocessAllImagesOutput {
  iteration: ImageIteration
}

type Mutation {
  addImage(input: CreateTagInput!): AddImageOutput
  reprocessAllImages: ReprocessAllImagesOutput
}
