schemas.content:
scalar JSON

type BaseItem {
  dbname: String
  name: String,
  description: String,
  value: Int
  weight: Float
  category: String
  detail: Detail
}

type AddCallback {
  message: String
  status: String
  id: ID
}

type DeleteCallback {
  message: String
  status: String
  rmCount: Int
}

input newBaseItem {
  dbname: String
  value: Int
  weight: Float
  category: String
  translations: translations
  detail: newDetail
}

type Detail {
  # gear + comsumable
  rating: Int
  type: String
  typeName: String
  equip: String
  equipI18n: [TranslatedEquip]
  effects: [String]
  effectsI18n: [TranslatedEffect]
  # book
  content: String
  contentDetail: String
}

type TranslatedEquip {
  equip: String
  name: String
}

type TranslatedEffect {
  effect: String
  name: String
}

input newDetail {
  # gear + comsumable
  rating: Int
  type: String
  equip: [String]
  effects: [String]
  # book
  content: String
}

input translations {
  name: JSON
  description: JSON
  bookContent: JSON
}

type Query {
  baseItem(dbname: String, lang: String): BaseItem
  baseItems(conditions: JSON, page: Int, lang: String): [BaseItem]
}

type Mutation {
  add(input: newBaseItem!): AddCallback
  delete(conditions: JSON): DeleteCallback
}

scalar JSON

type AddCallback {
  message: String
  status: String
  id: ID
}

type DeleteCallback {
  message: String
  status: String
  rmCount: Int
}

type Actor {
  dbname: String
  icon: String
  equiped: JSON
  name: String
  biography: String
}

input newActor {
  dbname: String
  icon: String
  equiped: JSON
  translations: translations
}

input translations {
  name: JSON
  biography: JSON
}

type Query {
  actors(conditions: JSON, page: Int, lang: String): [Actor]
  actor(dbname: String, lang: String): Actor
}

type Mutation {
  add(input: newActor!): AddCallback
  delete(conditions: JSON): DeleteCallback
}

scalar JSON

type BaseItem {
  dbname: String
  value: Int
  weight: Float
  category: String
  detail: JSON
}

type RefItem {
  refID: ID
  owner: String
  num: Int
  item: String
}

type InvVerboseItem {
  holder: String
  item: String
  refDetails: [RefItem]
  base: BaseItem
}

type InvItem {
  holder: String
  item: String,
  refs: [String]
}

type IRMResult {
  rmRefCount: Int
  rmInvCount: Int
}

type Query {
  invItems(conditions: JSON, page: Int, lang: String): [InvVerboseItem]
  invItem(itemName: String, holder: String, lang: String): InvVerboseItem
}

type Mutation {
  gift(itemName: String, holder: String, num: Int): InvItem
  remove(itemName: String!, holder: String!, num: Int): IRMResult
}
