schemas.content:
type User {
  id: Int!
  userName: String!
  firstName: String!
  lastName: String!
}

type Query {
  user(id: Int!): User!
  users: [User!]!
}

type Query {
  posts: [Post!]!
}

type Mutation {
  createPost(title: String!, desc: String!, author: String!): Boolean!
}

# Schema Types
type Post {
  id: String!
  title: String!
  desc: String!
  author: String!
  createdOn: String!
}
